import{_ as e,o as t,c as o,S as s}from"./chunks/framework.e7d5bd7a.js";const l="/assets/AmiyaBot-NapCatQQ-HTTP.784cc84f.png",n="/assets/AmiyaBot-NapCatQQ-WS.543ed46b.png",q=JSON.parse('{"title":"NapCatQQ QQ群机器人","description":"","frontmatter":{},"headers":[],"relativePath":"guide/deploy/instances/napcatqq.md","filePath":"guide/deploy/instances/napcatqq.md","lastUpdated":1762506263000}'),i={name:"guide/deploy/instances/napcatqq.md"};function p(r,a,c,d,b,u){return t(),o("div",null,[...a[0]||(a[0]=[s(`<h1 id="napcatqq-qq群机器人" tabindex="-1">NapCatQQ QQ群机器人 <a class="header-anchor" href="#napcatqq-qq群机器人" aria-label="Permalink to &quot;NapCatQQ QQ群机器人&quot;">​</a></h1><h2 id="初略流程" tabindex="-1">初略流程 <a class="header-anchor" href="#初略流程" aria-label="Permalink to &quot;初略流程&quot;">​</a></h2><ol><li>遵照其详细教程启动 NapCat 本体。</li><li>进入 WebUI，启动需要的 HTTP/WS 服务端。</li><li>配置 AmiyaBot 连接 NapCat。</li></ol><h2 id="启动方式" tabindex="-1">启动方式 <a class="header-anchor" href="#启动方式" aria-label="Permalink to &quot;启动方式&quot;">​</a></h2><p>NapCat 有着众多启动方式，每种方式各有优点：</p><ul><li><strong>Shell 版本</strong>：具有低内存、服务器部署简单的特点。</li><li><strong>Framework 版本</strong>：具有方便人机交互、便于窥屏的特点。</li></ul><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>略，详见 <a href="https://napneko.github.io/guide/install" target="_blank" rel="noreferrer">NapCatQQ 官方文档</a>。</p><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><h3 id="通过-webui-配置" tabindex="-1">通过 WebUI 配置 <a class="header-anchor" href="#通过-webui-配置" aria-label="Permalink to &quot;通过 WebUI 配置&quot;">​</a></h3><p>默认地址为 <code>0.0.0.0</code>，即监听所有地址。当配置了不可用的地址时将禁用 WebUI。</p><p>默认端口为 <code>6099</code>，当端口被设置为 <code>0</code> 时将禁用 WebUI。</p><p>启动后可在启动日志中看到形如 <code>[WebUi] WebUi Local Panel Url: http://127.0.0.1:6099/webui?token=xxxx</code> 的token信息。</p><p>也可打开 <code>webui.json</code> 文件，在其中找到token。</p><div class="language-webui.json"><button title="Copy Code" class="copy"></button><span class="lang">webui.json</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">{</span></span>
<span class="line"><span style="color:#babed8;">    &quot;host&quot;: &quot;0.0.0.0&quot;, // WebUI 监听地址</span></span>
<span class="line"><span style="color:#babed8;">    &quot;port&quot;: 6099, // WebUI 端口</span></span>
<span class="line"><span style="color:#babed8;">    &quot;token&quot;: &quot;xxxx&quot;, //登录密钥，默认是自动生成的随机登录密码</span></span>
<span class="line"><span style="color:#babed8;">    &quot;loginRate&quot;: 3, //每分钟登录次数限制</span></span>
<span class="line"><span style="color:#babed8;">}</span></span></code></pre></div><p>访问 <code>http://ip:port/webui/</code>，然后进行以下操作：</p><ol><li>进入 QQ 登录，点击 <code>QRCode</code> 进行二维码登录。</li><li>登录成功后，进入网络配置添加以下配置。 <ol><li>HTTP 配置如下图（看不清楚可以右键图片在新标签中打开） <img src="`+l+'" alt="AmiyaBot-NapCatQQ-HTTP"></li><li>websocket 配置如下图（看不清楚可以右键图片在新标签中打开） <img src="'+n+'" alt="AmiyaBot-NapCatQQ-WS"></li></ol></li><li>配置完成后，点击确认即可。</li></ol>',17)])])}const m=e(i,[["render",p]]);export{q as __pageData,m as default};
