import{_ as e,o as r,c as t,S as o}from"./chunks/framework.e7d5bd7a.js";const f=JSON.parse('{"title":"AmiyaBot 简介","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"guide/index.md","filePath":"guide/index.md","lastUpdated":1762506263000}'),i={name:"guide/index.md"};function n(p,a,h,d,s,l){return r(),t("div",null,[...a[0]||(a[0]=[o('<h1 id="amiyabot-简介" tabindex="-1">AmiyaBot 简介 <a class="header-anchor" href="#amiyabot-简介" aria-label="Permalink to &quot;AmiyaBot 简介&quot;">​</a></h1><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>AmiyaBot 是异步 Python QQ 机器人框架。基于 QQ <a href="https://bot.q.qq.com/wiki/develop/api-v2/" target="_blank" rel="noreferrer">官方API</a> 。内置了丰富的消息构建方法和适配器，让你轻松实现你的创意。</p><h3 id="她的过去" tabindex="-1">她的过去... <a class="header-anchor" href="#她的过去" aria-label="Permalink to &quot;她的过去...&quot;">​</a></h3><p>AmiyaBot 诞生于2019年，是一个<a href="https://ak.hypergryph.com/index" target="_blank" rel="noreferrer">《明日方舟》</a>民间 QQ 机器人，至本框架发布时已迭代了 5 个里程碑版本。一直以来，AmiyaBot 都秉着 <strong>“为所有人带来快乐”</strong> 的初衷，跌跌撞撞到现在，依旧坚持开源、免费为用户提供服务。</p><p>2022年5月13日重大变故后，AmiyaBot 决定迁移至 QQ 频道成为正式的机器人。原群聊机器人（Amiya2号）终止服务。<br> 2022年6月11日，剥离出其核心部分成为框架，也就是本文档所指的 <a href="/">AmiyaBot</a>。<br> 2022年9月14日，<a href="/guide/deploy/">V6 版本</a> 的用户自部署群聊机器人发布，由用户延续原群聊机器人（Amiya2号）的初衷。<br> 2024年6月18日，官方<a href="/guide/deploy/#官方版兔兔">全域机器人</a>正式发布。</p><h3 id="现在" tabindex="-1">现在 <a class="header-anchor" href="#现在" aria-label="Permalink to &quot;现在&quot;">​</a></h3><p>AmiyaBot 依旧坚持初心，将快乐带给所有人。加入我们的 <a href="https://qm.qq.com/q/9ft0w6HQj0" target="_blank" rel="noreferrer">官方群</a>、 <a href="https://qun.qq.com/qqweb/qunpro/share?_wv=3&amp;_wwv=128&amp;appChannel=share&amp;inviteCode=1W4sJux&amp;appChannel=share&amp;businessType=9&amp;from=181074&amp;biz=ka&amp;shareSource=5" target="_blank" rel="noreferrer">官方频道</a> 或 <a href="/guide/deploy/">开始使用</a>。</p>',8)])])}const c=e(i,[["render",n]]);export{f as __pageData,c as default};
